mapper.select:
-- as 起别名 --左外连接：left outer join （left join） 左外连接：就是把左边表的数据全部取出来，而右边表的数据有相等的，显示出来，如果没有，显示NULL select d.* , c.name as categoryName from dish d left outer join category c on d.category_id = c.id
--
and d.name like concat('%',#{name},'%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.create_time desc

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from dish d


                 d.category_id = #{categoryId}


         order by sort asc,create_time desc
